mapper.select:
SELECT board_id,version,subject,content,created_time,file_id,root_board_id
FROM board
WHERE board_id = #{board_id} AND version = #{version};

mapper.insert:
INSERT INTO board (board_id, version, subject, content, created_time, file_id, root_board_id) SELECT #{board_id}, #{version}, #{subject}, #{content}, CURRENT_TIMESTAMP, #{file_id},
(SELECT `AUTO_INCREMENT` FROM INFORMATION_SCHEMA.TABLES WHERE TABLE_SCHEMA = 'intern_2018_1' AND TABLE_NAME = 'board');
#{root_board_id};
SELECT board_id, created_time, root_board_id FROM board WHERE board_id =
LAST_INSERT_ID()
#{board_id}
AND version = #{version};

